export type RealPart = number;
export type ImagPart = number;
export type ComplexNumber = [RealPart, ImagPart];

export function fft(vector: number[]): ComplexNumber[];
export function fftInPlace(vector: number[]): void;

export namespace util {
    export function fftFreq(fftBins: ComplexNumber[], sampleRate: number): number[];
    export function fftMag(fftBins: ComplexNumber[]): number[];
}